package com.rays.excercise;

public class SmallerOfTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=9;
		if (a<b) {
			System.out.println("a is smaller than b");
		}
		else {
			System.out.println("b is smaller than a");
		}
	}

}
